mapper.select:
        select
        r_id,create_time,task_type,task_key1,task_key2,body_class,task_body,owner,status,execute_count,last_time,region_no,remark,fingerprint
        from schedule_task_${tableFix}
        where
        task_type=#{taskType,jdbcType=VARCHAR} 



	        and region_no in

	            #{region,jdbcType=INTEGER}



        and (status=#{statusInit,jdbcType=INTEGER} or
        (status=#{statusExecuting,jdbcType=INTEGER} and last_time < DATE_SUB(now(),INTERVAL ${lastTime} SECOND) ) or
        (status=#{statusError,jdbcType=INTEGER} and execute_count<=#{retryCount,jdbcType=INTEGER} and last_time <
        DATE_SUB(now(),INTERVAL ${lastTime} SECOND) )
        )
        limit 0,${fetchCount}

mapper.update:
update schedule_task_${tableFix} SET
status=#{status,jdbcType=INTEGER},last_time=now() where r_id in

    #{id,jdbcType=BIGINT}

UPDATE schedule_task_${tableFix} SET
STATUS =#{status,jdbcType=INTEGER}, execute_count=execute_count+1, last_time=now(), remark=#{remark,jdbcType=VARCHAR}
WHERE r_id=#{id,jdbcType=INTEGER}

UPDATE schedule_task_${tableFix} SET
STATUS =#{status,jdbcType=INTEGER}, last_time=now()
WHERE r_id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT
(SELECT COUNT(1) FROM schedule_task_${tableFix} WHERE task_type=#{taskType} and STATUS =0) AS backlogCount,
(SELECT COUNT(1) FROM schedule_task_${tableFix} WHERE task_type=#{taskType} and  STATUS =10
  AND execute_count >= #{dataRetryCount,jdbcType=INTEGER}
) AS errorCount

SELECT COUNT(1)
FROM schedule_task_${tableFix}
WHERE  task_type=#{taskType} and  status=0 AND execute_count=0 AND create_time <= DATE_SUB(now(),INTERVAL 60 SECOND);

SELECT r_id
FROM schedule_task_${tableFix}
WHERE status = 2 and task_type=#{taskType} and last_time < #{lastDate,jdbcType=TIMESTAMP} order by r_id limit 0,${fetchCount}
